import React, { useState } from "react";
import { dbService, storageService  } from "myFirebase";
import { doc, deleteDoc, updateDoc } from "firebase/firestore"
import { deleteObject, ref } from "firebase/storage";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash, faPencilAlt } from "@fortawesome/free-solid-svg-icons"
import 'post.css'
const Post = ({ postObj, isOwner }) => {
    const [editing, setEditing] = useState(false);
    const [newPost, setNwePost] = useState(postObj.text);
    const onDeleteClick = async () => {
        const ok = window.confirm("Are you sure you want to delete this post?");
        console.log(ok);
        if(ok){
            const PostTextRef = doc(dbService, "posts", `${postObj.id}`);
            // delete post
            await deleteDoc(PostTextRef);
            const urlRef = ref(storageService, postObj.attachmentURL)
            // delete file
            await deleteObject(urlRef);
        }
    }
    const toggleEditing = () => setEditing(prev => !prev);
    const onSubmit = async (event) => {
        event.preventDefault();
        const PostTextRef = doc(dbService, "posts", `${postObj.id}`);
        await updateDoc(PostTextRef,{
            text: newPost,
        })
        setEditing(false);
    }
    const onChange = (event) => {
        const {target:{value}} = event;
        setNwePost(value);
    }
    return (
        <div className="post">
            {editing ? (
                <>
                    <form onSubmit={onSubmit}
                    className="container postEdit">
                        <input type="text" value={newPost} onChange={onChange} required autoFocus className="formInput"/>
                        <input type="submit" value="Update Post" className="formBtn"/>
                    </form>
                    <span onClick={toggleEditing}
                    className="formBtn cancelBtn">Cencel</span>
                </>
                ) : (
                <>
                    <h4>{postObj.text}</h4>
                    {postObj.attachmentURL && (
                        <img src={postObj.attachmentURL}/>
                    )}
                    {isOwner && (
                    <div className="post__actions">
                        <span onClick={onDeleteClick}>
                            <FontAwesomeIcon icon={faTrash}/>
                            </span>
                        <span onClick={toggleEditing}>
                            <FontAwesomeIcon icon={faPencilAlt}/>
                            </span>
                    </div>
                    )}
                </>)}
        </div>
    )
};

export default Post;
